import React, { useState, useEffect } from 'react';
import './CartItems.css';
import remove_icon from '../Assets/cart_cross_icon.png';

const CartItems = () => {
  const [cart, setCart] = useState(null);
  const [error, setError] = useState(null);
  const [products, setProducts] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    if (!userId) {
      setError('User is not logged in');
      return;
    }

    // Fetch cart items for the user
    const fetchCart = async () => {
      try {
        const response = await fetch('http://localhost:5000/cart/get-cart', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ userId }), // Send the userId to the backend
        });

        const data = await response.json();
        console.log(data.cart.productsInCart)

        if (response.ok) {
          setCart(data.cart.productsInCart);  // Set the cart data if the response is successful
        } else {
          setError(data.message || 'Error fetching cart');
        }
      } catch (error) {
        setError('Error fetching cart');
      }
    };

    const fetchProductDetails = async (productIds) => {
        try {
          // Fetch product details for each productId in the cart
          const productDetailsPromises = productIds.map(productId =>
            fetch(`http://localhost:5000/product/${productId}`)
              .then(res => res.json())
              .then(data => data.product)
          );
  
          // Wait for all product details to be fetched
          const products = await Promise.all(productDetailsPromises);
          setProducts(products);  // Update the products state with fetched product details
        } catch (error) {
          setError('Error fetching product details');
        }
    };

    fet

    fetchCart();
  }, [userId]);

  if (error) {
    return <div>{error}</div>;
  }


  const handleRemoveItem = (productId) => {
    // Handle remove item logic (not implemented here)
    console.log(`Remove product with ID: ${productId}`);
  };

  return (
    <div className='cartitems'>
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {cart ? (
        cart.map((item, index) => {

            const product = products.find(p => p._id === item.productId);
          return (
            <div key={index}>
              <div className="cartitems-format cartitems-format-main">
                <img className='carticon-product-icon' src={product.image} alt={item.productName} />
                <p>{item.productName}</p>
                <p>${item.productPrice}</p>
                <button className='cartitems-quantity'>{item.productQty}</button>
                <p>${(item.productQty * item.productPrice).toFixed(2)}</p>
                <img
                  className='cartitems-remove-icon'
                  src={remove_icon}
                  alt="Remove"
                  onClick={() => handleRemoveItem(item.productId)}  // Handle item removal
                />
              </div>
              <hr />
            </div>
          );
        })
      ) : (
        <p>Your cart is empty</p>
      )}
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Total</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>${}</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>${}</h3>
            </div>
          </div>
          <button>PROCEED TO CHECKOUT</button>
        </div>
        <div className="cartitems-promocode">
          <p>If you have a promo code, enter it here</p>
          <div className="cartitems-promobox">
            <input type="text" placeholder='Promo code' />
            <button>Submit</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItems;
